          var defaultFillStyle = (body.isStatic ? '#14151f' : Common.choose(['#f19648', '#f5d259', '#f55a3c', '#063e7b', '#ececd1'])),
              defaultStrokeStyle = body.isStatic ? '#555' : '#ccc',
              defaultLineWidth = body.isStatic && body.render.fillStyle === null ? 1 : 0;
