  /**
   * Creates a new binary space partition
   */
  class BinarySpacePartition {
    constructor (params = {}) {
      this.params = Moar.defaultsDeep(params, {
        x: 0,
        y: 0,
        width: windowWidth,
        height: windowHeight,
        depth: 0
      });
      
      this.x = this.params.x;
      this.y = this.params.y;
      this.width = this.params.width;
      this.height = this.params.height;
      this.depth = this.params.depth;
      this.partitions = [];
      this.generate();

      return this
    }

    generate () {
      this.children = this.subdivide(0, this);
    }

    /**
     * Recursive method that subdivides the rectangle
     */
    subdivide (depth = 0, parent) {
      const partitions = [{}, {}, {}, {}];
      
      // Setup properties and maybe create children
      partitions.forEach(partition => {
        partition.isDivided = random() > Math.min(depth / 10 + .3, .9);
        partition.width = parent.width / 2;
        partition.height = parent.height / 2;
        partition.depth = depth;
      });
      
      // NW
      partitions[0].x = parent.x;
      partitions[0].y = parent.y;
      // NE
      partitions[1].x = parent.x + parent.width / 2;
      partitions[1].y = parent.y;
      // SE
      partitions[2].x = parent.x + parent.width / 2;
      partitions[2].y = parent.y + parent.height / 2;
      // SW
      partitions[3].x = parent.x;
      partitions[3].y = parent.y + parent.height / 2;
      
      // Setup children
      ++depth;
      partitions.forEach(partition => {
        if (partition.isDivided && depth < this.depth) {
          partition.children = this.subdivide(depth, partition);
        } else {
          this.partitions.push(partition);
        }
      });
      
      return partitions
    }
  }

